.sepetuyari{
            background: #74ad1c;
            padding: 0 15px;
            width: 90%;
            left: 5%;
            color: white;
            position: fixed;
            border-radius: 15px;
            line-height: 50px;
            font-size: 18px;
            text-align: center;
            top: 20px;
            z-index: 9999999;
            display: none;
        }




        .tabloduzen table th,td {
            border: 2px solid #e6e6e6;
        }
        .urunkutu {
            padding: 0;
            width: 32.3333333%;
            margin-right: 1.5%;
        }

        .thumbnail-variant-1 {
            margin-bottom: 35px
        }

        .thumbnail-variant-1 .caption h3:hover {
            text-decoration: underline
        }

        .thumbnail-variant-1 .caption .btn-block:hover {
            text-decoration: underline
        }

        .row .col-md-12 .urunkutu:nth-child(3n+0) {
            margin-right: 0
        }

        @media (min-width: 800px) {
            .section-wrapper .container .row {
                margin-left: unset;
                margin-right: unset;
            }

            .row .col-md-12 {
                padding: 0
            }
        }

        @media (max-width: 800px) {
            .urunkutu {
                padding: 0;
                width: 100%;
            }

            .thumbnail-variant-1 {
                margin-bottom: 30px
            }

            .section-wrapper.gray-bg {
                padding-top: 30px !important;
            }

            .thumbnail-variant-1 .caption {
                padding: 30px;
            }
        }

        .breadcrumb a:hover {
            text-decoration: underline
        }

        .thumbnail a img:hover {
            opacity: 0.5;
            filter: alpha(opacity=50);
        }

        .kategoriBaslik {
            color: #000;
            font-size: 30px;
            font-family: "Open Sans", HelveticaNeue, "Helvetica Neue", Helvetica, Arial, sans-serif;
            line-height: 32px;
            font-weight: 700;
        }

        .ozellik {
            color: #666666;
            font-size: 15px;
            font-family: "Open Sans", HelveticaNeue, "Helvetica Neue", Helvetica, Arial, sans-serif;;
            line-height: 22px;
        }
        .ozellik a{
            color: #FFFFFF;
        }
        .ozellik img{
            position: absolute;
            right: 0px;
            top:0px;
            height: 75px;
        }

        .ozellik ul {
            list-style: none;
            padding: 0;
        }

        .ozellik li {
            padding-left: 1.3em;
        }

        .ozellik li:before {
            content: "\f105"; /* FontAwesome Unicode */
            font-family: FontAwesome;
            color: #000;
            display: inline-block;
            margin-left: -1.3em; /* same as padding-left set on li */
            width: 1.3em; /* same as padding-left set on li */
            font-weight: normal;
        }

        #exTab1 .tab-content {
            color: exTab1;
            background-color: #fff;
            padding: 10px 15px;
        }

        #exTab2 h3 {
            color: black;
            background-color: #5aca83;
            padding: 10px 15px;

        }

        #exTab1 .nav-pills > li > a {
            border-radius: 0;
        }

        #exTab3 .nav-pills > li > a {
            border-radius: 4px 4px 0 0;
        }

        #exTab3 .tab-content {
            color: black;
            background-color: #fff;
            padding: 5px 15px;
        }

        .sertifika {
            background: white;
            padding: 15px;
            margin-top: 15px;
        }

        .sertifika > .title {
            color: black;
            font-size: 18px;
            font-weight: bold;
            margin-bottom: 10px;
            font-family: "Open Sans";
        }

        .sertifika > .item {
            border-bottom: 1px solid #ebebeb;
            padding: 10px 0px;
            font-family: "Open Sans";
            font-size: 16px;
        }

        .sertifika > .item:last-child {
            border: none
        }

        .pro-qty {
            position: relative;
            display: inline-block;
            width: 250px;
            height: 40px;
            border: 1px solid #ddd;
            margin-right: 15px;
        }

        .pro-qty2 {
            position: relative;
            display: inline-block;
            width: 250px;
            height: 40px;
            margin-right: 15px;
        }

        .pro-qty input {
            width: 100%;
            height: 100%;
            padding-right: 25px;
            padding-left: 20px;
            border: none;
        }

        .pro-qty select {
            width: 100%;
            height: 100%;
            padding-right: 25px;
            padding-left: 20px;
            border: none;
        }

        .pro-qty a {
            font-size: 18px;
            font-weight: normal;
            line-height: 20px;
            position: absolute;
            width: 20px;
            height: 20px;
            text-align: center;
        }

        .pro-qty a:hover {
            color: #3a9943;
        }

        .pro-qty a.inc {
            top: 0;
            right: 0;

            border-bottom: 1px solid #ddd;
            border-left: 1px solid #ddd;
        }

        .pro-qty a.dec {
            right: 0;
            bottom: 0;
            padding-top: 2px;
            border-left: 1px solid #ddd;
        }

        .quantity-title {
            font-size: 14px;
            font-weight: 500;
            line-height: 48px;
            color: #222;
            margin-right: 15px;
            display: inline-block;
            width: 50px;
        }

        .product-cart-button {
            line-height: 41px;
            padding: 0 70px;
            border-radius: 5px;
        }

        .theme-button {
            font-size: 16px;
            font-weight: 700;
            line-height: 50px;
            padding: 12px 30px;
            -webkit-transition: .3s;
            transition: .3s;
            text-align: center;
            color: #fff;
            border: none;
            background-color: #5ACA83;
        }

        .price {
            display: block;
            font-size: 30px;
            font-weight: bold;
            margin-top: 15px;
            color: #5aca83;
        }

        .amount-old {
            font-size: 24px;
            color: red;
            text-decoration: line-through;
        }
        .carousel-indicators{
            bottom: -20px;
        }
        .carousel-indicators li{
            border: 1px solid #00ADEF;
        }
        .carousel-indicators .active{
            background-color: #00ADEF;
        }






